mapper.insert:
INSERT INTO announcements (
    title, content, admin_id, is_sticky, status, created_at, updated_at, is_pinned
) VALUES (
    #{title}, #{content}, #{adminId}, #{isSticky}, #{status}, #{createdAt}, #{updatedAt}, #{isSticky}
)

mapper.select:
SELECT a.*, u.username 
FROM announcements a
LEFT JOIN users u ON a.admin_id = u.id
WHERE a.admin_id = #{adminId}
ORDER BY a.is_sticky DESC, a.created_at DESC
LIMIT #{pageSize} OFFSET #{offset}
